import React from 'react'
import PropTypes from 'prop-types'
import './../stylesheets/Book.css'
import { removeBook, editBook } from './../actions/actions'

const Book = (props, { store }) => {

  const remove = (e, id) => {
    e.preventDefault()
    store.dispatch(removeBook(id))
  }

  const edit = (e, id) => {
    e.preventDefault()
    let book = store.getState().books.filter((book) => book.id === id)
    debugger
    store.dispatch(editBook(book))
  }

  return (
    <div className='book'>
      <div className='book__inner'>
        <div className='book__title'>{props.title}</div>
        <div className='book__publication-date'>Publication date: {props.publicationDate ? props.publicationDate : 'unknown'}</div>
        <button className='book__remove app__button'
                title='Remove book'
                onClick={(e) => remove(e, props.id)}>&#10006;</button>
        <button className='book__edit app__button'
                title='Edit book'
                onClick={(e) => edit(e, props.id)}>&#9998;</button>
      </div>
    </div>
  )
}


Book.propTypes = {
  title: PropTypes.string.isRequired,
  publicationDate: PropTypes.number
}

Book.contextTypes = {
  store: PropTypes.object
}

export default Book
